import React, { useEffect, useState } from "react";
import axios from 'axios';
import './Heroes.css';

const Heroes = () => {
    const [heroes, setHeroes] = useState([]);
    const [selectedHero, setSelectedHero] = useState({id: '', name: ''});
    const [isLoading, setIsLoading] = useState(true);
    const [errorMessage, setErrorMessage] = useState('');

    const handleOnclick = (event) => {
       setSelectedHero({
           ...heroes,
           [evt.target.id]: evt.target.value
       })


    }
    useEffect(() => {
        let didCancel = false;
        setIsLoading(true);
        axios({
            method: 'GET',
            url: 'https://60dff0ba6b689e001788c858.mockapi.io/heroes'
        }).then(response => {
            if (!didCancel) {
                setIsLoading(false);
                setHeroes(response.data);
            }

        }).catch(error => {
            if (!didCancel) {
                setIsLoading(false);
                setErrorMessage(error.message);
            }

        })
        return () => {
            didCancel = true;
        }

    }, []);

    if (isLoading === true) { return 'Loading' }
    if (errorMessage) { return <div>{errorMessage}</div> }

    return (
        <div>
            {heroes.map(items => (
                <div>
                    <ul class="heroes">
                        <li style={{ backgroundColor: items.id === heroes.id ? 'black' : 'gray' }} onClick={handleOnclick}>
                            <span class="badge">{items.id}</span> {items.name}
                        </li>
                    </ul>
                </div>
            ))}

            {/* {heroes.find(hero => (
                <div >
                    <h2>{hero.name} Details</h2>
                    <div><span>id: </span>{hero.id}</div>
                    <div>
                        <label for="hero-name">Hero name: </label>
                        <input id="hero-name" placeholder="name" />
                    </div>
                </div>
            ))} */}

        </div >

    )
};
export default Heroes;
