import React, { useEffect, useState } from "react";
import axios from 'axios';
import './Heroes.css';

const Heroes = () => {
    const [heroes, setHeroes] = useState([]);
    const [selectedHero, setSelectedHero] = useState({ id: '', name: '' });
    const [isLoading, setIsLoading] = useState(true);
    const [errorMessage, setErrorMessage] = useState('');

    const onClickHandler =(event, value) => {
        setSelectedHero(value);
        console.log(selectedHero);

    }
    const onChangeHandler = (event,value) => {
        const heroListClone = [...heroes];
        heroListClone.find(items => items.id === selectedHero.id).name = value ;
        setSelectedHero(heroListClone);
    }
   
    useEffect(() => {
        let didCancel = false;
        setIsLoading(true);
        axios({
            method: 'GET',
            url: 'https://60dff0ba6b689e001788c858.mockapi.io/heroes'
        }).then(response => {
            if (!didCancel) {
                setIsLoading(false);
                setHeroes(response.data);
            }

        }).catch(error => {
            if (!didCancel) {
                setIsLoading(false);
                setErrorMessage(error.message);
            }

        })
        return () => {
            didCancel = true;
        }

    }, []);

    if (isLoading === true) { return 'Loading' }
    if (errorMessage) { return <div>{errorMessage}</div> }

    return (
        <div>
            {heroes.map((value, index) => (
                <div>
                    <ul class="heroes" onClick={(event) => onClickHandler(event, value)}>
                        <li style={{ backgroundColor: value.id === heroes.id ? 'black' : 'gray' }}

                        >
                            <span class="badge" onChange={onChangeHandler}>{value.id}</span> {value.name}
                        </li>
                    </ul>
                </div>
            ))}
            <form autoComplete="off">
                <h2>{selectedHero.name} Details</h2>
                <div><span>id: </span>{selectedHero.id}</div>
                <div>
                    <label for="hero-name">Hero name: </label>
                    <input class="input" id="hero-name" placeholder="name" value={selectedHero.name} onChange={(event) =>onChangeHandler(event)}/>
                </div>
            </form>



        </div >

    )
};
export default Heroes;
