    const [errorMessage, setErrorMessage] = useState('');
    const [clickedRow, setClickedRow] = useState(-1);
     
    const tblOnClickHandler=function(event,value,index){
        setClickedRow(index);
        setSelectedHero(value);
       
    }
    useEffect(() => {
        let didCancel = false;
        setIsLoading(true);
        axios({
            method: 'GET',
            url: 'https://60dff0ba6b689e001788c858.mockapi.io/heroes'
        }).then(response => {
            if (!didCancel) {
                setIsLoading(false);
                setHeroes(response.data);
            }

        }).catch(error => {
            if (!didCancel) {
                setIsLoading(false);
                setErrorMessage(error.message);
            }

