import React, { useEffect, useState } from "react";
import axios from 'axios';
import './Heroes.css';

const Heroes = () => {
    const [selectedHero, setSelectedHero] = useState({id: '', name: ''});
    const [isLoading, setIsLoading] = useState(true);
    const [errorMessage, setErrorMessage] = useState('');
    
    useEffect(() => {
        let didCancel = false;
        setIsLoading(true);
        axios({
            method: 'GET',
            url: 'https://60dff0ba6b689e001788c858.mockapi.io/heroes'
        }).then(response => {
            if (!didCancel) {
                setIsLoading(false);
                const { data } = response;
                console.log(data);
                setSelectedHero(data);
            }

        }).catch(error => {
            if (!didCancel) {
                setIsLoading(false);
                setErrorMessage(error.message);
            }

        })
        return () => {
            didCancel = true;
        }

    }, []);

    if (isLoading === true) { return 'Loading' }
    if (errorMessage) { return <div>{errorMessage}</div> }

    return (
        <div>
            <h1>Tour of Heroes</h1>
            <h2>My Heroes</h2>
            {selectedHero.map(heroes => (
                <div    >
                    <ul class="heroes">
                        <li style={{backgroundColor: heroes.id === selectedHero.id ? 'black' : 'gray' }}>
                            <span class="badge">{heroes.id}</span> {heroes.name}
                        </li>
                    </ul></div>

            ))}</div>
    )
};
export default Heroes;
