import React, { useEffect, useState } from "react";
import axios from 'axios';
import './Heroes.css';

const Heroes = () => {
    const [heroes, setHeroes] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [errorMessage, setErrorMessage] = useState('');
    useEffect(() => {
        let didCancel = false;
        setIsLoading(true);
        axios({
            method: 'GET',
            url: 'https://60dff0ba6b689e001788c858.mockapi.io/heroes'
        }).then(response => {
            if (!didCancel) {
                setIsLoading(false);
                const { data } = response;
                console.log(data);
                setHeroes(data);
            }

        }).catch(error => {
            if (!didCancel) {
                setIsLoading(false);
                setErrorMessage(error.message);
            }

        })
        return () => {
            didCancel = true;
        }

    }, []);

    if (isLoading === true) { return 'Loading' }
    if (errorMessage) { return <div>{errorMessage}</div> }

    return (
        <div className="heroes">
            {heroes.map(items => (
                <div>
                    <ul class="heroes">
                        <li style={{ backgroundColor: items.id === heroes.id ? 'black' : 'gray' }}>
                            <span class="badge">{items.id}</span> {items.name}
                        </li>
                    </ul>
                </div>
            ))}
            
                {heroes.find(hero => (
                    <div >
                     <h2>{heroes.name} Details</h2>
                     <div><span>id: </span>{heroes.id}</div>
                     <div>
                         <label for="hero-name">Hero name: </label>
                         <input id="hero-name" placeholder="name" />
                     </div>
                     </div>
                ))}
               
            </div >
        
    )
};
export default Heroes;
