import React from "react";

const HeroDetails = ({clickedRow,selectedHero,setSelectedHero,heroes,setHeroes}) => {
    
    
    const onChangeHandler = (event) => {
        const { name, value } = event.target;
        setSelectedHero({
            ...selectedHero,
            [name]: value,
        });
    }
    const onSubmitHandler = (event) => {
        event.preventDefault();
    }
    return (
        <div>
           
                <form onSubmit={onSubmitHandler} autoComplete="off">
                    <h2  onChange={onChangeHandler}>{heroes.name} Details</h2>
                    <div><span  onChange={onChangeHandler}>id: </span>{heroes.id}</div>
                    <div>
                        <label for="hero-name">Hero name: </label>
                        <input id="hero-name" placeholder="name"  onChange={onChangeHandler} />
                    </div>
                </form>
            
        </div>
    )
};
export default HeroDetails;
